mapper.select:
WITH A AS ( SELECT DISTINCT ${var}, barcode, date FROM model.mars_${data_source}_promo_scaledrd_date
WHERE
${c_list}
) SELECT ${var},sum(quantity) quantity FROM model.mars_${data_source}_sku_day_format format INNER JOIN A ON A.barcode=format.barcode AND A.date = format.pay_date WHERE date > #{last_date}::TIMESTAMP + interval '-1 ${monthOrWeek}' GROUP BY ${var} ORDER BY quantity DESC

WITH A AS ( SELECT DISTINCT product_name, barcode, date FROM model.mars_${data_source}_promo_scaledrd_date
WHERE
${c_list}
) SELECT product_name, pay_date, sum(quantity) quantity FROM model.mars_${data_source}_sku_day_format format INNER JOIN A ON A.barcode=format.barcode AND A.date = format.pay_date WHERE date > #{last_date}::TIMESTAMP + interval '-1 ${monthOrWeek}' GROUP BY product_name, pay_date ORDER BY quantity DESC

WITH A AS (
SELECT DISTINCT ${var}, barcode, date FROM model.mars_${data_source}_promo_scaledrd_date


        WHERE

            ${c_list}



),now AS (
SELECT ${var},sum(quantity) quantity FROM model.mars_${data_source}_sku_day_format format INNER JOIN A
ON A.barcode=format.barcode AND A.date = format.pay_date WHERE
date > #{last_date}::TIMESTAMP + interval '-1 ${monthOrWeek}'
GROUP BY ${var}
),hb AS(
SELECT ${var},sum(quantity) quantity FROM model.mars_${data_source}_sku_day_format format INNER JOIN A
ON A.barcode=format.barcode AND A.date = format.pay_date WHERE
date > #{last_date}::TIMESTAMP + interval '-2 ${monthOrWeek}'
AND date <= #{last_date}::TIMESTAMP + interval '-1 ${monthOrWeek}'
GROUP BY ${var}
)
SELECT
now.${var},
(now.quantity - hb.quantity)/hb.quantity as hbResult
FROM now LEFT JOIN hb ON now.${var}=hb.${var}
ORDER BY hbResult DESC

WITH A AS (
SELECT DISTINCT ${var}, barcode, date FROM model.mars_${data_source}_promo_scaledrd_date


        WHERE

            ${c_list}



),now AS (
SELECT ${var},sum(quantity) quantity FROM model.mars_${data_source}_sku_day_format format INNER JOIN A
ON A.barcode=format.barcode AND A.date = format.pay_date WHERE
date > #{last_date}::TIMESTAMP + interval '-1 ${monthOrWeek}'
GROUP BY ${var}
),tb AS (
SELECT ${var},sum(quantity) quantity FROM model.mars_${data_source}_sku_day_format format INNER JOIN A
ON A.barcode=format.barcode AND A.date = format.pay_date WHERE
date > #{last_date}::TIMESTAMP + interval '-1 ${monthOrWeek}' + interval '-1 year'
AND date <= #{last_date}::TIMESTAMP + interval '-1 year'
GROUP BY ${var}
)
SELECT
now.${var},
(now.quantity - tb.quantity)/tb.quantity as tbResult
FROM now LEFT JOIN tb ON now.${var}=tb.${var}
ORDER BY tbResult DESC

WITH A AS ( SELECT barcode, ppg_id FROM model.mars_${data_source}_promo_scaledrd_date
WHERE
${c_list}
), effect AS ( SELECT pred.date, sum(pred.predacvbasevol) AS predacvbasevol, sum(exp(reduced.coef_model1_coupon * pred.coupon)) AS effect_coupon, sum(exp(reduced.coef_model1_debut * pred.debut)) AS effect_debut, sum(exp(reduced.coef_model1_discount * pred.discount)) AS effect_discount, sum(exp(reduced.coef_model1_ln_baseprice * pred.ln_baseprice)) AS effect_ln_baseprice FROM model.mars_${data_source}_preddata pred LEFT JOIN model.mars_${data_source}_coeffs_final_reduced reduced ON pred.ppg_id = reduced.ppg_id WHERE pred.ppg_id IN (SELECT ppg_id FROM A) GROUP BY pred.date ORDER BY pred.date ), q AS ( SELECT pay_date, sum(quantity - predacvbasevol) AS actual, sum(quantity) AS quantity FROM (model.mars_${data_source}_sku_day_format format INNER JOIN A ON format.barcode = A.barcode) INNER JOIN model.mars_${data_source}_preddata pred ON A.ppg_id = pred.ppg_id AND format.pay_date = pred.date GROUP BY pay_date ORDER BY pay_date ) SELECT date, effect_coupon, effect_debut, effect_discount, effect_ln_baseprice, (predacvbasevol - effect_coupon - effect_debut - effect_discount - effect_ln_baseprice) AS other, actual, quantity FROM effect LEFT JOIN q ON effect.date = q.pay_date
