mapper.select:
WITH A AS ( SELECT ppg_id FROM stg.d_${data_source}_promo_scaledrd_date
WHERE
${c_list}
) SELECT avg(exp(reduced.coef_model1_coupon * pred.coupon)) AS effect_coupon, avg(exp(reduced.coef_model1_debut * pred.debut)) AS effect_debut, avg(exp(reduced.coef_model1_discount * pred.discount)) AS effect_discount, avg(exp(reduced.coef_model1_ln_baseprice * pred.ln_baseprice)) AS effect_ln_baseprice FROM stg.d_${data_source}_preddata pred LEFT JOIN stg.d_${data_source}_coeffs_final_reduced reduced ON pred.ppg_id = reduced.ppg_id WHERE pred.ppg_id IN (SELECT ppg_id FROM A) AND date > #{last_date}::TIMESTAMP + interval '-1 month'
